% plot(t,alpha_100,t,alpha_075,t,alpha_050,t,alpha_025,t,alpha_000)

clear
clc

r=0:0.1:10;
H=0.5;
q_inf=3;
r0=1.5;


r1=r0+1/H*(q_inf-r0);

for i=1:length(r)
%LINEAR
q_lin(i)=r0+H*(r(i)-r0);
if q_lin(i)>q_inf
   q_lin(i)=q_inf;
end
A=1.00;
q_exp_100(i)=q_inf-(q_inf-r0)*exp(A*(1-r(i)/r0));
A=0.75;
q_exp_075(i)=q_inf-(q_inf-r0)*exp(A*(1-r(i)/r0));
A=0.50;
q_exp_050(i)=q_inf-(q_inf-r0)*exp(A*(1-r(i)/r0));
A=0.25;
q_exp_025(i)=q_inf-(q_inf-r0)*exp(A*(1-r(i)/r0));
A=0.1;
q_exp_010(i)=q_inf-(q_inf-r0)*exp(A*(1-r(i)/r0));
end
figure(30)
plot(r,q_lin,r,q_exp_100,r,q_exp_050,r,q_exp_010)
ylim([0 4])
title('Linear vs. Exponential hardening law')
xlabel('r') % x-axis label
ylabel('q') % y-axis label
legend('Linear','Exp, A=1.00','Exp, A=0.50','Exp, A=0.10')
